class TestPeriodicSpline1D:

    #---------------
    def test_init_PeriodicSpline1D (
        self
    ):
        default_periodic_spline_1d = PeriodicSpline1D()
        assert 1 == default_periodic_spline_1d.period
        assert 0 == default_periodic_spline_1d.degree
        assert 0.0 == default_periodic_spline_1d.delay
        assert [0.0] == default_periodic_spline_1d.spline_coeff

