            known_data = test[5]
            c = np.array(data, dtype = "float")
            ps = PeriodicSpline1D.from_spline_coeff(c, degree = degree, delay = delay)
            c = np.array(known_data, dtype = "float")
            known_bs = PeriodicSpline1D.from_spline_coeff(c, degree = known_degree, delay = known_delay)
