            b = bs.spline_coeff
            for (k, ck) in enumerate(known_bs.spline_coeff):
                assert isclose(
                    ck,
                    b[k],
                    rel_tol = sqrt(ulp(1.0)),
                    abs_tol = sqrt(ulp(1.0))
                )

